package Sample_0328;


public class For_statement {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i=1; i<=10; i++){
			System.out.println(i);
		}
		
		for(int i=10; i>=1; i--){
			System.out.println(i*10);
		}
		
		int i=10;
		while(i>=1){
			System.out.println(i*10);
			i--;
		}
		
	}

}


